import React, {Component} from 'react';
//import './grid.css';
import Box from './box';
class Grid extends Component{

  constructor(props, context){
  	super(props, context);

  	this.state = {
  		generation:0,
  		gridArray: Array(this.props.rows).fill().map(() => Array(this.props.cols).fill(0)),
  		run: false
  	}
  	this.intervalId = 0;
  	this.active = new Map();
  }

  selectBox = (i,j) => {
  	var gridArray = this.state.gridArray;
  	if(gridArray[i][j] === 0){
  		gridArray[i][j] = 1;
  		if(this.active.has(i)){
  			this.active.get(i).set(j,1);
  		}
  		else{
  			this.active.set(i,new Map());
  			this.active.get(i).set(j,1);
  		}
  	}
  	else{
  		gridArray[i][j] = 0;
  		if(this.active.has(i)){
  			this.active.get(i).delete(j);
  			if(this.active.get(i).size === 0)
  				this.active.delete(i);
  		}
  	}
  	this.setState({
  		gridArray:gridArray
  	});
  	console.log(this.active);
  }

  reset = () =>{
  	this.setState({
  		generation:0,
  		gridArray:Array(this.props.rows).fill().map(() => Array(this.props.cols).fill(0))
  	});
  }

  startEvolution = () =>{
  	var gen = this.state.generation;
  	var rows = this.props.rows;
  	var cols = this.props.cols;
	var gA = this.state.gridArray;
	var nbr = Array(rows).fill().map(() => Array(cols).fill(0));
