		for( var j=0;j<cols;j++){
			for(var x=-1;x<=1;x++){
				for(var y=-1;y<=1;y++){
					if(!(y===0&&x===0)){
						if(gA[(i+x+rows)%rows][(j+y+cols)%cols] === 1)
							nbr[i][j]++;
					}
				}
			}
		}
    }
    for(i=0;i<rows;i++){
   		for(j=0;j<cols;j++){
   			if(gA[i][j] === 1){
   				if(nbr[i][j]<2)
   					gA[i][j] = 0;
   				else if(nbr[i][j]>3){
   					gA[i][j] = 0;
   				}
   			}
   			else{
   				if(nbr[i][j] === 3)
   					gA[i][j] = 1;
   			}
   		}
   	}
   	gen++;
   	this.setState({
   		gridArray: gA,
   		generation: gen
   	});
  }
