import React, {Component} from 'react';
//import './grid.css';
import Box from './box';
class Grid extends Component{
  constructor(props, context){
  	super(props, context);

  	this.state = {
  		generation:0,
  		gridArray: Array(this.props.rows).fill().map(() => Array(this.props.cols).fill(0)),
  		run: false
  	}
  }

  selectBox = (i,j) => {
  	var gridArray = this.state.gridArray;
  	if(gridArray[i][j] === 0)
  		gridArray[i][j] = 1;
  	else
  		gridArray[i][j] = 0;
  	this.setState({
  		gridArray:gridArray
  	});
  }

  reset = () =>{
  	this.setState({
  		generation:0,
  		gridArray:Array(this.props.rows).fill().map(() => Array(this.props.cols).fill(0))
  	});
  }
  sleep = (milliseconds)=>{
	  const date = Date.now();
	  let currentDate = null;
	  do {
	    currentDate = Date.now();
	  } while (currentDate - date < milliseconds);
	}
  startEvolution = () =>{
  	var gen = this.state.generation;
  	var rows = this.props.rows;
  	var cols = this.props.cols;
  	for(var k=0;k<1;k++){
  		var gA = this.state.gridArray;
  		var nbr = Array(rows).fill().map(() => Array(cols).fill(0));
  		for(var i=0;i<rows;i++){
	   		for( var j=0;j<cols;j++){
	   			for(var x=-1;x<=1;x++){
	   				for(var y=-1;y<=1;y++){
	   					if(!(y==j&&x==i)){
	   						if(gA[(i+x+rows)%rows][(j+y+cols)%cols] == 1)
	   							nbr[i][j]++;
	   					}
	   				}
	   			}
	   		}
	    }
	    console.log(nbr);
	    for(var i=0;i<rows;i++){
	   		for( var j=0;j<cols;j++){
	   			if(gA[i][j] == 1){
	   				if(nbr[i][j]<2)
	   					gA[i][j] = 0;
	   				else if(nbr[i][j]>3){
	   					gA[i][j] = 0;
	   				}
	   			}
	   			else{
	   				if(nbr[i][j] == 3)
	   					gA[i][j] = 1;
	   			}
	   		}
	   	}
	   	gen++;
	   	this.setState({
	   		gridArray: gA,
	   		generation: gen
	   	});
  	}

  }
  render(){
  	const width = this.props.cols*17;
  	var box_array = [];
	for(var i=0;i<this.props.rows;i++){
   		for( var j=0;j<this.props.cols;j++){
   			//var id = i+"_"+j;
   			if(this.state.gridArray[i][j] === 0)
   				box_array.push(<Box boxClass="box off" x={i} y={j} selectBox={this.selectBox}/>);
   			else
   				box_array.push(<Box boxClass="box on" x={i} y={j} selectBox={this.selectBox}/>);
   		}
       }
    return (
    	<div>
	      <div className="row" style={{marginBottom:10}}>
		      <div className="col-md-3"></div>
		      <div className="col-md-2">
		      	<button type="button" className="btn btn-primary" onClick={this.reset}>Reset</button>
		      </div>
		      <div className="col-md-2">
		      	<button type="button" className="btn btn-success" onClick={this.startEvolution}>Start</button>
		      </div>
		      <div className="col-md-2">
		      	<button type="button" className="btn btn-danger">Stop</button>
		      </div>
		      <div className="col-md-3"></div>
		   </div>
	      <div className="row">
	      	  <div className="col-md-12" >
			      <div className="grid row" style={{width:width,margin:"auto",lineHeight:0}}>
			     		{box_array}
			      </div>
		      </div>
	      </div>
	      <div className="row">
		      <div className="col-md-4"></div>
	      	  <div className="col-md-4" >
			      <h3 style={{margin:"auto" }}>Generation : {this.state.generation}</h3>
		      </div>
		      <div className="col-md-4"></div>
	      </div>
	   
      </div>
    );
  };
}

export default Grid;
