  	this.intervalId = 0;
  	this.active = new Map();
  }

  selectBox = (i,j) => {
  	var gridArray = this.state.gridArray;
  	if(gridArray[i][j] === 0){
  		gridArray[i][j] = 1;
  		if(this.active.has(i)){
  			this.active.get(i).set(j,1);
  		}
